let currentCount = 0;
const [countStatus, counterStatus] = useState("");
const [addOne, addOneCount] = useState("");

function counter(){

  //return the counter status
  counterStatus(()=>{
    return currentCount;
  });



};

addOneCount(()=> console.log("clicked add button"));


